create table cadastro (id int8 generated by default as identity, cargo int4, entidade_associada int8, foto varchar(255), identificacao varchar(255), nome varchar(255), telefone_celular varchar(255), telefone_fixo varchar(255), tipo_cadastro int4, endereco_id int8, primary key (id));
create table cidade (id int8 generated by default as identity, nome varchar(255), uf varchar(255), primary key (id));
create table endereco (id int8 generated by default as identity, cep varchar(255), gps varchar(255), logradouro varchar(255), numero int8, cidade_id int8, primary key (id));
alter table if exists cadastro add constraint FKh9n62cqmcy3n5w8uu4t2naqwc foreign key (endereco_id) references endereco;
alter table if exists endereco add constraint FK8b1kcb3wucapb8dejshyn5fsx foreign key (cidade_id) references cidade;
